import { useRouter } from 'next/navigation';
import { getCurrentUserId, getSession, logoutUser } from '../../lib/auth';

export default function UserProfile() {
  const [user, setUser] = useState<{ id: string; email: string; name?: string } | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const loadUser = async () => {
      try {
        // Wait a bit to ensure session is properly established
        await new Promise(resolve => setTimeout(resolve, 300));

        // First check localStorage directly
        let userData = null;
        if (typeof window !== 'undefined' && typeof localStorage !== 'undefined') {
          const storedSession = localStorage.getItem('user_session');
          if (storedSession) {
            try {
              const parsedSession = JSON.parse(storedSession);
              userData = parsedSession.user;
              console.log('Loaded user from localStorage:', userData); // Debug log
            } catch (parseError) {
              console.error('Error parsing stored session:', parseError);
            }
          }
        }

        // If not found in localStorage, try getSession
        if (!userData) {
          const session = await getSession();
          console.log('UserProfile session data from getSession:', session); // Debug log
          if (session?.user) {
            userData = session.user;
          }
        }

        if (userData) {
          setUser(userData);
        } else {
          setUser(null);
        }
      } catch (error) {
        console.error('Error loading user:', error);
        setUser(null);
      } finally {
        setLoading(false);
      }
    };

    loadUser();

    // Listen for storage changes to update user state across tabs
    const handleStorageChange = () => {
      console.log('Storage change detected'); // Debug log
      loadUser();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const handleLogout = async () => {
    try {
      const success = await logoutUser();
      if (success) {
        // Clear local storage
        if (typeof window !== 'undefined' && typeof localStorage !== 'undefined') {
          localStorage.removeItem('user_session');
        }
        // Redirect to login
        router.push('/login');
        router.refresh();
      }
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center space-x-2">
        <div className="w-8 h-8 rounded-full bg-gray-700 animate-pulse"></div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex items-center">
        <span className="text-gray-400 text-sm">Not logged in</span>
      </div>
    );
  }

  return (
    <div className="flex items-center space-x-3">
      <div className="flex items-center space-x-2">
        <div className="w-8 h-8 rounded-full bg-gradient-to-r from-yellow-500 to-yellow-600 flex items-center justify-center">
          <span className="text-white text-xs font-medium">
            {user.name ? user.name.charAt(0).toUpperCase() : user.email.charAt(0).toUpperCase()}
          </span>
        </div>
        <span className="hidden md:inline text-gray-300 text-sm max-w-[120px] truncate" title={user.email}>
          {user.name || user.email.split('@')[0]}
        </span>
      </div>

      <button
        onClick={handleLogout}
        className="px-3 py-1.5 bg-gray-800 hover:bg-gray-700 text-gray-300 text-xs rounded-lg transition-colors"
      >
        Logout
      </button>
    </div>
  );
}"}

